	0% {
		opacity: 0;
		-webkit-transform: scale3d(1, 0, 1);
		transform: scale3d(1, 0, 1);
	}
	50% {
		transform: scale3d(1, 1.2, 1);
		-webkit-transform: scale3d(1, 1.2, 1);
		opacity: 1;
	}
	100% {
		-webkit-transform: scale3d(1, 1, 1);
		transform: scale3d(1, 1, 1);
	}
}

